    width:100,
    flexDirection:'row',
    justifyContent:'center',
    alignItem:'center',
  },
  head5:{
    color: colors.text3,
    fontSize:20,
    fontWeight: '200',
    marginLeft:10,
  },
  btnout:{
    width:'100%',
    justifyContent:'center',
    alignItems:'center',
    // marginTop:10,
    flexDirection:'row',
  },
  btntxt:{
    color: 'white',
    paddingHorizontal:10,
    paddingVertical:5,
    fontSize:20,
    borderRadius:10,
    width:'90%',
    textAlign:'center',
  },
  container2:{
    width: '90%',
    backgroundColor: colors.col1,
    padding:20,
    borderRadius:20,
    alignSelf: 'center',
    marginVertical:5,
    elevation:10,
    alignItems:'center',
  },
  txt1:{
    color: colors.text1,
    fontSize:20,
    fontWeight:'200',
  },
  txt2:{
    color: colors.text3,
    fontSize:30,
    fontWeight:'200',
    marginVertical:10,
  },
  container2in:{
    flexDirection:'row',
    alignItems: 'center',
  },
  txt3:{
    color: 'red',
    fontSize:16,
    width:'30%',
    textAlign: 'center',
  },
  dash:{
    width:1,
    height:20,
    backgroundColor: colors.text1,
    marginHorizontal:10,
  },
  container3:{
    width:'100%',
    alignItems:'center',
    // justifyContent:'center',
  },
  txt5:{
    color: 'red',
    fontSize:18,
    // width:'30%',
    textAlign: 'center',
  },
  container4:{
    width:'90%',
    alignSelf:'center',
    alignItems:'center',
  },
  c4in:{
    flexDirection:'row',
    justifyContent:'space-evenly',
    width:'100%',
