import React, { useState } from 'react';
import { Button, StyleSheet, Text, View, TextInput, Alert, Platform } from 'react-native';
import { colors, titles, btn1 } from "../src/globals/style";
import { AntDesign, Octicons } from '@expo/vector-icons';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { useNavigation } from "@react-navigation/native";
import { AuthContext } from '../services/AuthContext';
import { getMenuData } from '../services/MenuService';



export default function LoginScreen() {
  const [username, setUsername] = useState('a@e.com');
  const [password, setPassword] = useState('123');
  const [showpassword, setShowpassword] = useState(false);
  const [emailfocus, setEmailFocus] = useState(false);
  const [passwordfocus, setPasswordfocus] = useState(false);


  const { signIn } = React.useContext(AuthContext);



  async function attemptLogin() {
    try {
      await signIn({ username, password });
    } catch (error) {
      if (Platform.OS !== "web") {
        Alert.alert(error.toString());
      } else {
        alert(error);
      }
    }
  }

  return (
    <View style={styles.container}>
      <Text style={styles.head1}>Bean Scene Ordering System</Text>
      <View style={styles.inputout}>
        
        <AntDesign name="user" size={24} color={emailfocus === true ? colors.text1 : colors.text2} />
        <Text style={styles.label}>Login</Text>
        <TextInput
          style={styles.input}
          placeholder="Username"
          onFocus={()=>{
            setEmailFocus(true)
            setPasswordfocus(false)
            setShowpassword(false)
            }} 
          onChangeText={(text) => setUsername(text)}
        />
        <TextInput
          style={styles.input}
          placeholder="Password"
          onChangeText={(text) => setPassword(text)}
          onFocus={()=>{
            setEmailFocus(false)
            setPasswordfocus(true)
          }}
          secureTextEntry= {showpassword === false ? true : false }
        />
        <Button title="Login" onPress={(e) => attemptLogin()} />
      </View>

    </View>
  )
}






const styles = StyleSheet.create({
  // container: {
  //   flex: 1,
  //   backgroundColor: '#fff',
  //   alignItems: 'center',
  //   justifyContent: 'center',
  //   padding: 20,
  // },
  // title: {
  //   fontSize: 24,
  //   fontWeight: 'bold',
  //   marginBottom: 20,
  // },
  // formContainer: {
  //   width: '100%',
  //   marginBottom: 20,
  // },
  // label: {
  //   fontSize: 18,
  //   marginBottom: 10,
  // },
  // input: {
  //   height: 40,
  //   borderColor: 'gray',
  //   borderWidth: 1,
  //   marginBottom: 10,
  //   paddingHorizontal: 10,
  // },

  container: {
    flex: 1,
    width: '100%',
    alignItems: "center",
    justifyContent: "center",

  },
  head1: {
    fontSize: titles.title1,
    color: colors.text1,
    textAlign: 'center',
    marginVertical: 10,
  },
  inputout: {
    flexDirection: 'row',
    width: '80%',
    marginVertical: 10,
    backgroundColor: colors.col1,
    borderRadius: 10,
    paddingHorizontal: 10,
    paddingVertical: 10,
    alignSelf: 'center',
    //elevation: 20, //only android
    boxShadow: '2px 2px 10px #888888', //web use
  },
  input: {
    fontSize: 18,
    marginLeft: 10,
    width: '80%',
    outlineStyle: "none",
  }
});
