import { useNavigation } from '@react-navigation/native';
import { useFocusEffect } from '@react-navigation/native';
import React, { useState } from 'react';
import { Alert, View, StyleSheet, StatusBar, Text, Button, TextInput,TouchableOpacity } from 'react-native';
import { useRoute } from '@react-navigation/native';
import { colors } from '../src/globals/style';
import HomeHeadNav from '../src/components/HomeHeadNav';
import Backbtn from '../src/components/Backbtn';
import BottomNav from '../src/components/BottomNav';


export default function OrderDetails( ) {

    const route = useRoute();
    const {orderData} = route.params;
   
   
    console.log(orderData);


    async function signOutFromApp() {
        signOut();
    }

    return (

        <View style={styles.container}>
        <StatusBar/>
        <HomeHeadNav navigation={navigation} title={"Order"}/>
        <Backbtn navigation={navigation} redirectPage='Order'/>
        <BottomNav navigation={navigation}/>
        <Text style={styles.title}>Your Order has been placed!</Text>
        <Text style={styles.title}>Order Detail</Text>
        <Text style={styles.label}>Order ID:</Text>
        <Text style={styles.text}>{orderData.id}</Text>
        <Text style={styles.label}>Table ID:</Text>
        <Text style={styles.text}>{orderData.tableId}</Text>
        <Text style={styles.label}>Items:</Text>
        {orderData.items.map((item) => (
          <View key={item.id} style={{ flexDirection: "row" }}>
            <Text style={styles.item}>
              {item.itemName}
            </Text>
            <Text style={styles.item}>

              X {item.qty}
            </Text>
          </View>
        ))}
      </View>
           
           
      

               
    )     
}

const styles = StyleSheet.create({
    container: {
    flex: 1,
    backgroundColor: '#fff',
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  label: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  text: {
    fontSize: 16,
    marginBottom: 10,
  },
  item: {
    fontSize: 16,
    marginLeft: 20,
  },

});
