import { useFocusEffect } from '@react-navigation/native';
import React, { useState } from 'react';
import { StatusBar,Alert, View, StyleSheet, FlatList, Text, Button, TextInput, TouchableOpacity } from 'react-native';
import { getOrderData } from '../services/OrderService';
import { signOut } from '../services/LoginApiService';
import { AuthContext } from '../services/AuthContext';
import { startNewOrder } from '../services/OrderService';
import { useRoute } from '@react-navigation/native';
import { useNavigation } from '@react-navigation/native';
import { getTableData } from '../services/OrderService';
import { colors, btn1, btntxt1, titles } from '../src/globals/style';

import HomeHeadNav from '../src/components/HomeHeadNav';
import BottomNav from '../src/components/BottomNav';


export default function OrderScreen({ navigation, route }) {
    const [data, setData] = useState([])

    const [Tabledata, setTableData] = useState([])
    // const { menuItem } = route.params;
    // // console.log(menuItem);

    const { signOut } = React.useContext(AuthContext);

    const [tableId, setTableId] = useState([]);

    async function signOutFromApp() {
        signOut();
    }


    async function fetchData() {
        try {
            const orderData = await getOrderData();
            setData(orderData);
        } catch (error) {
            Alert.alert(error.toString());
        }
    }

    useFocusEffect(
        React.useCallback(() => {
            fetchData();

            return () => { };
        }, [])
    );

    async function fetchTableData() {


        try {
            const tableData = await getTableData();
            setTableData(tableData);

        } catch (error) {
            Alert.alert(error.toString());
        }
    }

    useFocusEffect(
        React.useCallback(() => {
            fetchTableData();

            return () => { };
        }, [])
    );

    return (
        <View style={styles.container}>

    


            <Button title="Sign Out" onPress={(e) => signOutFromApp()} />
            

            <Button title="View Order" onPress={() => navigation.navigate('OrderList')} />

            <StatusBar/>
            <HomeHeadNav navigation={navigation} title={"Order"}/>
            <BottomNav navigation={navigation}/>
            <Text style={styles.box}>Select a Table: </Text>
        

            <FlatList
                data={Tabledata}
                numColumns={10} // Set the number of columns to 2 for a row layout
                keyExtractor={(item) => item.id.toString()}

                renderItem={({ item }) => (
                    <TouchableOpacity
                        style={styles.cardout}
                        onPress={() => {
                            navigation.navigate("MenuTest", { tableId: item.number });
                            //   save(item.number); // Pass item.number as an argument to the save function
                        }}
                    >

                    <View style={styles.box1}>
                        <Text style={styles.txt1}>{item.number}</Text>

                    </View>
                    </TouchableOpacity>
                )}
            />
         
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        marginTop: 10,
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'stretch',
        justifyContent: 'center',
    },

    red: {
        color: 'red',
    },

    tableContainer: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'space-between',
        paddingHorizontal: 10,
    },
    tableItem: {
        width: '5%', // Adjust the width based on your desired layout
        aspectRatio: 1, // Maintain a square aspect ratio for the table item
        backgroundColor: 'lightblue',
        borderRadius: 10,
        marginVertical: 10,
        justifyContent: 'center',
        alignItems: 'center',
    },
    tableItemText: {
        fontSize: 18,
        fontWeight: 'bold',

    },


    cardout: {
        display:'flex',
        //width:"95%",
        //backgroundColor:'red',
    },
    box: {
        backgroundColor: colors.col1,
        width: '95%',
        margin:5,
        padding:10,
        borderRadius: 10,
        boxShadow: '1px 1px 5px #888888', //web use
        alignItems:'center',
        justifyContent:'center',
        textAlign:'center',
        fontSize:18,
    },    
      box1: {
        backgroundColor: colors.col1,
        margin:5,
        padding:10,
        borderRadius: 5,
        boxShadow: '1px 1px 5px #888888', //web use
        alignItems:'center',
        fontSize:18,
      },   
    footerout:{
        flexDirection:'row',
        alignItems:'center',
        justifyContent: 'center',
        margin:10,
    },


});
