import { useNavigation } from '@react-navigation/native';
import { useFocusEffect } from '@react-navigation/native';
import React, { useState } from 'react';
import { Alert, View, StyleSheet, FlatList, Text, Button, TextInput,TouchableOpacity } from 'react-native';

export default function OrderDetails({ route }) {
    const [data, setData] = useState([])

    


    const { selectedItemName } = route.params;

   
   

    const { signOut } = React.useContext(AuthContext);



    async function signOutFromApp() {
        signOut();
    }

  
    
    return (
        <View style={styles.container}>
            {/* <Button title="Sign Out" onPress={(e) => signOutFromApp()} /> */}
            
            <Text>Current Order Detail:</Text>
            <Text>Your Order is created successfully!</Text>
           
            <Text>Items Order:{selectedItemName}</Text>
             
                  
               
            
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        marginTop: 10,
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'stretch',
        justifyContent: 'center',
    },

    red: {
        color: 'red',
    },

    tableContainer: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'space-between',
        paddingHorizontal: 10,
      },
      tableItem: {
        width: '5%', // Adjust the width based on your desired layout
        aspectRatio: 1, // Maintain a square aspect ratio for the table item
        backgroundColor: 'lightblue',
        borderRadius: 10,
        marginVertical: 10,
        justifyContent: 'center',
        alignItems: 'center',
      },
      tableItemText: {
        fontSize: 18,
        fontWeight: 'bold',
        
      },


});
