import React, { useState, useEffect } from 'react';
import { View, StyleSheet, FlatList, Text, Alert, Button, TouchableOpacity, TextInput } from 'react-native';
import { useNavigation, useRoute } from '@react-navigation/native';
import { startNewOrder } from '../services/OrderService';
import { getMenuData } from '../services/MenuService';
import { hr80, incdecbtn, incdecinput, incdecout } from '../src/globals/style';
import { colors } from '../src/globals/style';

const renderProduct = ({ item, addItemToOrder }) => {
  const [quantity, setQuantity] = useState(1);

  const handleQuantityChange = (newQuantity) => {
    setQuantity(newQuantity);
  };

  return (
    <TouchableOpacity style={styles.menuItem} onPress={() => addItemToOrder(item, quantity)}>
      <Text style={styles.menuItemText}>{item.itemName}</Text>

      <View style={styles.quantityContainer}>
        <TouchableOpacity style={styles.quantityButton} onPress={() => handleQuantityChange(quantity - 1)}>
          <Text style={styles.quantityButtonText}>-</Text>
        </TouchableOpacity>
        <TextInput
          style={styles.quantityInput}
          value={quantity.toString()}
          onChangeText={handleQuantityChange}
          keyboardType="numeric"
        />
        <TouchableOpacity style={styles.quantityButton} onPress={() => handleQuantityChange(quantity + 1)}>
          <Text style={styles.quantityButtonText}>+</Text>
        </TouchableOpacity>
      </View>
    </TouchableOpacity>
  );
};

export default function MenuTestScreen({ route }) 
{
  //navigation
  const navigation = useNavigation();


  const { tableId } = route.params;

  //state
  const [products, setProducts] = useState([])
  const [selectedItems, setSelectedItems] = useState([]);

  const addItemToOrder = (item, quantity) => {
    // Check if the item already exists in selectedItems
    const existingItemIndex = selectedItems.findIndex((orderItem) => orderItem.id === item.id);
  
    if (existingItemIndex !== -1) {
      // If the item exists, update the quantity of the existing item
      const updatedItems = [...selectedItems];
      updatedItems[existingItemIndex].qty = quantity;
  
      setSelectedItems(updatedItems);
    } else {
      // If the item does not exist, add it to selectedItems with the given quantity
      setSelectedItems((prevItems) => [...prevItems, { ...item, qty: quantity }]);
    }
  };
  

  async function save() {

    const order = {

      TableId: tableId,
      Items: selectedItems,

    }

    try {
      const orderData = await startNewOrder(order);
      navigation.navigate('OrderDetail', { orderData });


      // update state for order data
      const newOrderData = data.concat(orderData);
      setData(newOrderData);


      Alert.alert('The order was saved successfully!');
    } catch (error) {
      // const validationErrors = error.validationErrors;
      // const errorMessage = validationErrors.join('\n');
      //Alert.alert(errorMessage);
    }
  }

  useEffect(() => {
    getMenuData().then(setProducts).catch(e => Alert.alert(e));
  }, []);

  
   


  

  return <View style={styles.container}>
    <Text>Table ID: {tableId}</Text>
    <FlatList 
    data={products} 
    renderItem={({ item }) => renderProduct({ item, addItemToOrder })}
     

    keyExtractor={(item) => item.id} />

    <Button title='Place order' onPress={save}


    />
  </View>



}

