import { useFocusEffect } from '@react-navigation/native';
import React, { useState } from 'react';
import { Alert, View, StyleSheet, FlatList, Text, Button, TextInput, TouchableOpacity } from 'react-native';
import { getOrderData } from '../services/OrderService';
import { signOut } from '../services/LoginApiService';
import { AuthContext } from '../services/AuthContext';
import { startNewOrder } from '../services/OrderService';
import { useRoute } from '@react-navigation/native';
import { useNavigation } from '@react-navigation/native';
import { getTableData } from '../services/OrderService';

export default function OrderScreen({ navigation, route }) {
    const [data, setData] = useState([])

    const [Tabledata, setTableData] = useState([])
    // const { menuItem } = route.params;
    // // console.log(menuItem);

    const { signOut } = React.useContext(AuthContext);

    const [tableId, setTableId] = useState([]);

    async function signOutFromApp() {
        signOut();
    }

    async function save(Id) {

        const order = {

            TableId: Id,

        }

        try {
            const orderData = await startNewOrder(order);

            // update state for order data
            const newOrderData = data.concat(orderData);
            setData(newOrderData);

            Alert.alert('The order was saved successfully!');
        } catch (error) {
            const validationErrors = error.validationErrors;
            const errorMessage = validationErrors.join('\n');
            Alert.alert(errorMessage);
        }
    }


    async function fetchData() {
        try {
            const orderData = await getOrderData();
            setData(orderData);
        } catch (error) {
            Alert.alert(error.toString());
        }
    }

    useFocusEffect(
        React.useCallback(() => {
            fetchData();

            return () => { };
        }, [])
    );

    async function fetchTableData() {


        try {
            const tableData = await getTableData();
            setTableData(tableData);

        } catch (error) {
            Alert.alert(error.toString());
        }
    }

    useFocusEffect(
        React.useCallback(() => {
            fetchTableData();

            return () => { };
        }, [])
    );

    return (
        <View style={styles.container}>

<StatusBar />
        <HomeHeadNav navigation={navigation} title={"Table"}/>
        <BottomNav navigation={navigation}/>
        <Text style={styles.box}>Select a Table: </Text>


            <Button title="Sign Out" onPress={(e) => signOutFromApp()} />
            <Text>      </Text>

            <Button title="View Order" onPress={() => navigation.navigate('OrderList')} />


            <Text style={styles.tableItemText}>Table List</Text>

            <FlatList
                data={Tabledata}
                numColumns={10} // Set the number of columns to 2 for a row layout
                keyExtractor={(item) => item.id.toString()}

                renderItem={({ item }) => (
                    <TouchableOpacity
                        style={styles.tableItem}
                        onPress={() => {
                            navigation.navigate("Menu", { tableId: item.number });
                            //   save(item.number); // Pass item.number as an argument to the save function
                        }}
                    >
                        <Text style={styles.tableItemText}>{item.number}</Text>
                    </TouchableOpacity>
                )}
            />
            {/* 
            {/* <Text style={styles.red} >Create New Order: Please enter Table Id</Text>
            <TextInput placeholder='Enter TableId' onChangeText={(text) => setTableId(text)} />
         
            <Button title='Save' onPress={(e) => save()} /> */}

            {/* <Text>Current Order Detail:</Text>
             <FlatList data={data}
                renderItem={({ item }) =>
                    <View style={{ flexDirection: 'row' }}>
                        <Text>Table :{item.tableId}
                            {item.items.map((orderItem, index) => (
                                <Text key={index}> {orderItem.itemName} </Text>
                            ))}
                        </Text>
                        { <Button title='Order Details' onPress={(e) => navigation.navigate("Menu",{ orderId: item.tableId })} /> }
                    </View>
                } keyExtractor={(item, index) => item.id.toString() }
            />   */}
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        marginTop: 10,
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'stretch',
        justifyContent: 'center',
    },

    red: {
        color: 'red',
    },

    tableContainer: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'space-between',
        paddingHorizontal: 10,
    },
    tableItem: {
        width: '5%', // Adjust the width based on your desired layout
        aspectRatio: 1, // Maintain a square aspect ratio for the table item
        backgroundColor: 'lightblue',
        borderRadius: 10,
        marginVertical: 10,
        justifyContent: 'center',
        alignItems: 'center',
    },
    tableItemText: {
        fontSize: 18,
        fontWeight: 'bold',

    },


});
