import React, { useState, useEffect } from 'react';
import { View, StyleSheet, FlatList, Text, Alert, Button, TouchableOpacity, TextInput } from 'react-native';
import { useNavigation, useRoute } from '@react-navigation/native';
import { startNewOrder } from '../services/OrderService';
import { getMenuData } from '../services/MenuService';
import { hr80, incdecbtn, incdecinput, incdecout } from '../src/globals/style';
import { colors } from '../src/globals/style';


const styles = StyleSheet.create({
  // container: {
  //   flex: 1,
  //   backgroundColor: '#fff',
  //   alignItems: 'stretch',
  //   justifyContent: 'center',
  // },
  container: {
    flex:1,
    backgroundColor:'#fff',
    //alignItem:'center',
    width:"100%",
  },
  container1:{
    //position:'absolute',
    top:5,
    flex:1,
    backgroundColor:"#fff",
    alignItem:'center',
    justifyContent:'center',
  },
  s1:{
    width:'100%',
    height:300,
    backgroundColor:'#fff',
    alignItems:'center',
    justifyContent:'center',
  },
  cardimgin:{
    width:'100%',
    height:'100%',
  },
  s2:{
    width:'100%',
    padding:20,
  },
  s2in:{
    flexDirection:'row',
    justifyContent:'space-between',
    alignItems:'center',
    marginBottom:10,
  },
  head1:{
    fontSize:20,
    fontWeight:'500',
    color:colors.text1,
    width:220,
    marginRight:10,
  },
  head2:{
    fontSize:20,
    fontWeight:'200',
    color:colors.text3,
  },
  s3:{
    width:'95%',
    backgroundColor: colors.text1,
    padding:20,
    borderRadius:10,
    marginLeft:'2.5%',
  },
  head3:{
    fontSize:30,
    fontWeight:'200',
    color:colors.col1,
  },
  head4:{
    marginVertical:10,
    fontSize:20,
    fontWeight:'400',
    color: colors.col1,
  },
  s3in:{
    backgroundColor: 'white',
    padding:10,
    borderRadius:10,
    width:100,
    flexDirection:'row',
    justifyContent:'center',
    alignItem:'center',
  },
  head5:{
    color: colors.text3,
    fontSize:20,
    fontWeight: '200',
    marginLeft:10,
  },
  btnout:{
    width:'100%',
    justifyContent:'center',
    alignItems:'center',
    // marginTop:10,
    flexDirection:'row',
  },
  btntxt:{
    color: 'white',
    paddingHorizontal:10,
    paddingVertical:5,
    fontSize:20,
    borderRadius:10,
    width:'90%',
    textAlign:'center',
  },
  container2:{
    width: '90%',
    backgroundColor: colors.col1,
    padding:20,
    borderRadius:20,
    alignSelf: 'center',
    marginVertical:5,
    elevation:10,
    alignItems:'center',
  },
  txt1:{
    color: colors.text1,
    fontSize:20,
    fontWeight:'200',
  },
  txt2:{
    color: colors.text3,
    fontSize:30,
    fontWeight:'200',
    marginVertical:10,
  },
  container2in:{
    flexDirection:'row',
    alignItems: 'center',
  },
  txt3:{
    color: 'red',
    fontSize:16,
    width:'30%',
    textAlign: 'center',
