import React, { useState } from 'react';
import { Button, StyleSheet, Text, View, TextInput, Alert, Platform } from 'react-native';
import { colors, titles, btn1 } from "../srcglobals/style";
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { useNavigation } from "@react-navigation/native";
import { AuthContext } from '../services/AuthContext';
import { getMenuData } from '../services/MenuService';

export default function LoginScreen() {
    const [username, setUsername] = useState('a@e.com');
    const [password, setPassword] = useState('123');
    const [showpassword, setShowpassword] = useState(false);


    const { signIn } = React.useContext(AuthContext);



    async function attemptLogin() {
      try {
        await signIn({ username, password });
      } catch (error) {
        if (Platform.OS !== "web") {
          Alert.alert(error.toString());
        } else {
          alert(error);
        }
      }
    }

    return (
         <View style={styles.container}>
         <Text style={styles.head1}>Bean Scene Ordering System</Text>
         <View style={styles.inputout}>
         <AntDesign name="user" size={24} color={emailfocus === true ? colors.text1 : colors.text2} />
         <Text style={styles.label}>Login</Text>
         <TextInput
            style={styles.input}
              placeholder="Username"
               onChangeText={(text) => setUsername(text)}
             />
             <TextInput
               style={styles.input}
               placeholder="Password"
               onChangeText={(text) => setPassword(text)}
               secureTextEntry
             />
             <Button title="Login" onPress={(e) => attemptLogin()} />
           </View>
          
         </View>
    )}


      
      


