      flex: 1,
      width: '100%',
      alignItems: "center",
      justifyContent: "center",

  },
  head1:{
      fontSize: titles.title1,
      color:colors.text1,
      textAlign: 'center',
      marginVertical:10,
  },
  inputout:{
      flexDirection:'row',
      width:'80%',
