import React, { useState, useEffect } from 'react';
import { View, StyleSheet, FlatList, Text, Alert, Button, TouchableOpacity, TextInput } from 'react-native';
import { useNavigation, useRoute } from '@react-navigation/native';
import { startNewOrder } from '../services/OrderService';
import { getMenuData } from '../services/MenuService';
import { hr80, incdecbtn, incdecinput, incdecout } from '../src/globals/style';
import { colors } from '../src/globals/style';

export default function MenuScreen({ route }) {
  //navigation
  const navigation = useNavigation();

  const { tableId } = route.params;

  //state
  const [products, setProducts] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);

  const addItemToOrder = (item, quantity) => {
    // add item if it does not already exist
    let existingItem = selectedItems.find((orderItem) => orderItem.id === item.id);

    if (existingItem) {
      const updatedItem = {
        ...existingItem,
        qty: existingItem.qty + quantity
      };

      const updatedItems = selectedItems.map((orderItem) =>
        orderItem.id === updatedItem.id ? updatedItem : orderItem
      );

      setSelectedItems(updatedItems);
    } else {
      setSelectedItems((prevItems) => [...prevItems, { ...item, qty: quantity }]);
    }
  };

  async function save() {
    const order = {
      TableId: tableId,
      Items: selectedItems
    }

    try {
      // Replace 'startNewOrder' and 'data' with your actual implementation
      const orderData = await startNewOrder(order);
      navigation.navigate('OrderDetail', { orderData });

      Alert.alert('The order was saved successfully!');
    } catch (error) {
      Alert.alert('An error occurred while saving the order.');
    }
  }

  useEffect(() => {
    // Replace 'getMenuData' with your actual implementation
    getMenuData()
      .then(setProducts)
      .catch((error) => Alert.alert('An error occurred while fetching the menu data.'));
  }, []);

  const renderProduct = ({ item }) => {
    const [quantity, setQuantity] = useState(1);

    const increaseQuantity = () => {
      setQuantity(quantity + 1);
    };

    const decreaseQuantity = () => {
      if (quantity > 1) {
        setQuantity(quantity - 1);
      }
    };

    return (
      <TouchableOpacity style={styles.menuItem} onPress={() => addItemToOrder(item, quantity)}>
        <Text style={styles.menuItemText}>{item.itemName}</Text>
        <View style={styles.quantityContainer}>
          <TouchableOpacity style={styles.quantityButton} onPress={decreaseQuantity}>
            <Text style={styles.quantityButtonText}>-</Text>
          </TouchableOpacity>
          <Text style={styles.quantity}>{quantity}</Text>
          <TouchableOpacity style={styles.quantityButton} onPress={increaseQuantity}>
            <Text style={styles.quantityButtonText}>+</Text>
          </TouchableOpacity>
        </View>
      </TouchableOpacity>
    );
  };

  return (
    <View style={styles.container}>
      <Text>Table ID: {tableId}</Text>
      <FlatList data={products} renderItem={renderProduct} keyExtractor={(item) => item.id} />
      <Button title="Place order" onPress={save} />
    </View>
  );
}
