import React, { useState, useEffect } from 'react';
import { StatusBar,View, StyleSheet, FlatList, Text, Alert, Button, TouchableOpacity, TextInput } from 'react-native';
import { useNavigation, useRoute } from '@react-navigation/native';
import { startNewOrder } from '../services/OrderService';
import { getMenuData } from '../services/MenuService';
import { hr80, incdecbtn, incdecinput, incdecout } from '../src/globals/style';
import { colors } from '../src/globals/style';
import HomeHeadNav from '../src/components/HomeHeadNav';
import BottomNav from '../src/components/BottomNav';

export default function MenuScreen({ route }) {
  //navigation
  const navigation = useNavigation();


  const { tableId } = route.params;

  //state
  const [products, setProducts] = useState([])
  const [selectedItems, setSelectedItems] = useState([]);




const addItemToOrder = (item, quantity) => {
  setSelectedItems((prevItems) => [...prevItems, { ...item, qty: quantity }]);
};

const increaseQuantity = (itemId) => {
  setSelectedItems((prevItems) => {
    return prevItems.map((item) => {
      if (item.id === itemId) {
        return { ...item, qty: item.qty + 1 };
      }
      return item;
    });
  });
};

const decreaseQuantity = (itemId) => {
  setSelectedItems((prevItems) => {
    return prevItems.map((item) => {
      if (item.id === itemId && item.qty > 1) {
        return { ...item, qty: item.qty - 1 };
      }
      return item;
    });
  });
};

  async function save() {

    const order = {

      TableId: tableId,
      Items: selectedItems,

    }

    try {
      const orderData = await startNewOrder(order);
      navigation.navigate('OrderDetail', { orderData });


      // update state for order data
      const newOrderData = data.concat(orderData);
      setData(newOrderData);


      Alert.alert('The order was saved successfully!');
    } catch (error) {
      // const validationErrors = error.validationErrors;
      // const errorMessage = validationErrors.join('\n');
      //Alert.alert(errorMessage);
    }
  }

  useEffect(() => {
    getMenuData().then(setProducts).catch(e => Alert.alert(e));
  }, []);

  const renderProduct = p => {
    let item = p.item;

    const quantity = selectedItems.find((selectedItem) => selectedItem.id === item.id)?.qty || 0;

  

     return <TouchableOpacity style={styles.menuItem} onPress={() => addItemToOrder(item,quantity)}>
      <StatusBar/>
      <HomeHeadNav navigation={navigation} title={"Order"}/>
      <BottomNav navigation={navigation}/>
       <Text style={styles.menuItemText}>{item.itemName}</Text>
       <View style={styles.container3}>
             <View style={hr80}></View>
             <Text style={styles.txt5}>Food Quantity</Text>
             <View style={incdecout}>
          <Text style={incdecbtn} onPress={() => increaseQuantity(item.id)}>
            +
          </Text>
          <TextInput style={incdecinput} value={quantity.toString()} />
          <Text style={incdecbtn} onPress={() => decreaseQuantity(item.id)}>
            -
          </Text>
        </View>
           <View style={hr80}></View>
         </View>
     </TouchableOpacity>
   }


  return <View style={styles.container}>
    <Text style>Table ID: {tableId}</Text>
    <FlatList 
    data={products} 
    renderItem={renderProduct} 
    keyExtractor={(item) => item.id} />

    <Button title='Place order' onPress={save}


    />
  </View>






}

const styles = StyleSheet.create({
  // container: {
  //   flex: 1,
  //   backgroundColor: '#fff',
  //   alignItems: 'stretch',
  //   justifyContent: 'center',
  // },
  container: {
    flex:1,
    backgroundColor:'#fff',
    //alignItem:'center',
    width:"100%",
  },
  container1:{
    //position:'absolute',
    top:5,
    flex:1,
    backgroundColor:"#fff",
    alignItem:'center',
    justifyContent:'center',
  },


  box: {
    backgroundColor: colors.col1,
    width: '95%',
    margin:5,
    padding:10,
    borderRadius: 10,
    boxShadow: '1px 1px 5px #888888', //web use
    alignItems:'center',
    justifyContent:'center',
    textAlign:'center',
    fontSize:18,
},    
  s1:{
    width:'100%',
    height:300,
    backgroundColor:'#fff',
    alignItems:'center',
    justifyContent:'center',
  },
  cardimgin:{
    width:'100%',
    height:'100%',
  },
  s2:{
    width:'100%',
    padding:20,
  },
  s2in:{
    flexDirection:'row',
    justifyContent:'space-between',
    alignItems:'center',
    marginBottom:10,
  },
  head1:{
    fontSize:20,
