    fontWeight:'400',
    color: colors.col1,
  },
  s3in:{
    backgroundColor: 'white',
    padding:10,
    borderRadius:10,
    width:100,
    flexDirection:'row',
    justifyContent:'center',
    alignItem:'center',
  },
  head5:{
    color: colors.text3,
    fontSize:20,
