    marginLeft:10,
  },
  btnout:{
    width:'100%',
    justifyContent:'center',
    alignItems:'center',
    // marginTop:10,
    flexDirection:'row',
  },
  btntxt:{
    color: 'white',
    paddingHorizontal:10,
    paddingVertical:5,
    fontSize:20,
    borderRadius:10,
    width:'90%',
    textAlign:'center',
  },
  container2:{
    width: '90%',
    backgroundColor: colors.col1,
    padding:20,
    borderRadius:20,
    alignSelf: 'center',
    marginVertical:5,
    elevation:10,
    alignItems:'center',
  },
  txt1:{
