import { useNavigation } from '@react-navigation/native';
import { useFocusEffect } from '@react-navigation/native';
import React, { useState } from 'react';
import { Alert, View, StyleSheet, StatusBar, Text, Button, TextInput, TouchableOpacity } from 'react-native';
import { useRoute } from '@react-navigation/native';
import { colors, hr80 } from '../src/globals/style';
import HomeHeadNav from '../src/components/HomeHeadNav';
import Backbtn from '../src/components/Backbtn';
import BottomNav from '../src/components/BottomNav';


export default function OrderDetails({ navigation }) {

  const route = useRoute();
  const { orderData } = route.params;
  console.log(orderData);

  return (

    <View style={styles.container}>
      <StatusBar />
      <HomeHeadNav navigation={navigation} title={"Order"} />
      <Backbtn navigation={navigation} redirectPage='Order' />
      <BottomNav navigation={navigation} />
      <View style={styles.container}>

      <Text style={styles.title}>Your Order has been placed!</Text>
      <Text style={hr80}></Text>
      <View style={styles.statusout}>
        <Text style={styles.head1}>Order Detail</Text>
        <View style={styles.row}>
          <Text style={styles.left}>Order ID:</Text>
          <Text style={styles.right}>{orderData.id}</Text>
        </View>
        <View style={styles.row}>
          <Text style={styles.left}>Table ID:</Text>
          <Text style={styles.right}>{orderData.tableId}</Text>
        </View>
      </View>
      <Text style={hr80}></Text>
      <Text style={styles.head1}>Items:</Text>
      {orderData.items.map((item) => (
        <View key={item.id} style={styles.rowout}>
          <View style={styles.row}>
          <View style={styles.left}>
                    <Text style={styles.qty}>{item.qty}</Text>
                    <Text style={styles.title}>{item.item}</Text>
                    <Text style={styles.price}>${item.price}</Text>
                  </View>
                  <View style={styles.right}>
                    <Text style={styles.totalprice}>
                        ${parseInt(item.qty)*parseInt(item.itemName)}                         
                    </Text>
                  </View>
          </View>
          <Text style={styles.item}>
            {item.itemName}
          </Text>
          <Text style={styles.item}>
            X {item.qty}
          </Text>
        </View>
      ))}
    </View>
    
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    width:"100%",
    flexDirection: 'column',
    alignItems: 'center',
  },
  head1: {
    fontSize: 30,
    fontWeight: '200',
    color: colors.text1,
    margin: 10,
    textAlign: 'center',
  },
  c1: {
    width: '95%',
  },
  row: {
    flexDirection: 'row',
    alignItems: 'center',
    marginVertical: 5,
    justifyContent: 'space-between',
  },
  rowout: {
    flexDirection: 'column',
    margin: 10,
    boxShadow: '2px 2px 5px #888888', //web use
    backgroundColor: colors.col1,
    padding: 5,
    borderRadius: 10,
  },
  left: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  right: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  qty: {
    width: 40,
    height: 30,
    backgroundColor: colors.text1,
    borderRadius: 5,
    textAlign: 'center',
    // textAlignVertical:'center',
    marginRight: 10,
    color: 'white',
    fontSize: 17,
    fontWeight: 'bold',
  },
  title: {
    fontSize: 17,
    fontWeight: 'bold',
    marginRight: 10,
  },
  price: {
    fontSize: 17,
    fontWeight: 'bold',
    marginRight: 10,
    color: colors.text1,
  },
  totalprice: {
    fontSize: 17,
    fontWeight: 'bold',
    borderColor: colors.text1,
    borderRadius: 5,
    borderWidth: 1,
    padding: 5,
  },
  btntext: {
    fontSize: 20,
    fontWeight: 'bold',
    color: 'white',
    margin: 10,
    whiteSpace: 'nowrap',
  }
  //   container: {
  //   flex: 1,
  //   backgroundColor: '#fff',
  //   padding: 20,
  // },
  // title: {
  //   fontSize: 24,
  //   fontWeight: 'bold',
  //   marginBottom: 10,
  // },
  // label: {
  //   fontSize: 18,
  //   fontWeight: 'bold',
  //   marginBottom: 5,
  // },
  // text: {
  //   fontSize: 16,
  //   marginBottom: 10,
  // },
  // item: {
  //   fontSize: 16,
  //   marginLeft: 20,
  // },

});
